CREATE PROCEDURE [dbo].[sp_User_ChangePassword]
	@IUserId	int,
	@IPassword	VARCHAR(400)
AS

INSERT INTO [User]
(
	Type,
	[User],
	Password,
	Name,
	Status,
	Token
)
VALUES
(
	@IType,
	@IUser,
	@IPassword,
	@IName,
	@IStatus,
	@IToken
)

SET @OUserId = @@IDENTITY

